package omar.cucumber.backend.test

class Application {

    static void main(String[] args) {

        if (System.getenv("runEnv") == "local")
        {
            CucumberTest backend = new CucumberTest()
            backend.startTest()
        } else {
            TomcatStart tomcat = new TomcatStart()
            tomcat.startServer()
        }
    }
}